import React, { useState } from 'react';
import './App.css';
import ReactPlayer from 'react-player'
import Duration from './Duration'

interface Props {
  url: any | null
  pip: boolean
  playing: boolean
  controls: boolean
  light: boolean
  volume: number
  muted: boolean
  played: number
  loaded: number
  duration: number
  playbackRate: number
  loop: boolean
}

const VIDEO_URL = '//player.vimeo.com/video/575873877'

function App() {

  const [ state, setState ] = useState<Props>({
    url: VIDEO_URL,
    pip: false,
    playing: false,
    controls: false,
    light: false,
    volume: 0.8,
    muted: false,
    played: 0,
    loaded: 0,
    duration: 0,
    playbackRate: 1.0,
    loop: false
  })

  const load = (url:any) => {
    setState({
      ...state,
      url,
      played: 0,
      loaded: 0,
      pip: false
    })
  }

  const handlePlayPause = () => {
    setState({ 
      ...state,
      playing: !state.playing
    })
  }

  const handleStop = () => {
    setState({ 
      ...state,
      url: null, 
      playing: false 
    })
  }

  const handleToggleControls = () => {
    const url = state.url
    setState({
      controls: !state.controls,
      url: null
    }, () => load(url))
  }

  const handleToggleLight = () => {
    setState({ ...state, light: !state.light })
  }

  const handleToggleLoop = () => {
    setState({ ...state, loop: !state.loop })
  }

  const handleVolumeChange = (e:any) => {
    setState({ ...state, volume: parseFloat(e.target.value) })
  }

  const handleToggleMuted = () => {
    setState({ ...state, muted: !state.muted })
  }

  const handleSetPlaybackRate = (e:any) => {
    setState({ ...state, playbackRate: parseFloat(e.target.value) })
  }

  const handleTogglePIP = () => {
    setState({ ...state, pip: !state.pip })
  }

  const handlePlay = () => {
    console.log('onPlay')
    setState({ ...state, playing: true })
  }

  const handleEnablePIP = () => {
    console.log('onEnablePIP')
    setState({ ...state, pip: true })
  }

  const handleDisablePIP = () => {
    console.log('onDisablePIP')
    setState({ ...state, pip: false })
  }

  const handlePause = () => {
    console.log('onPause')
    setState({ ...state, playing: false })
  }

  const handleSeekMouseDown = (e:any) => {
    setState({ ...state, seeking: true })
  }

  const handleSeekChange = (e:any) => {
    setState({ ...state, played: parseFloat(e.target.value) })
  }

  const handleSeekMouseUp = (e:any) => {
    setState({ ...state, seeking: false })
    player.seekTo(parseFloat(e.target.value))
  }

  const handleProgress = (state:any) => {
    console.log('onProgress', state)
    // We only want to update time slider if we are not currently seeking
    if (!state.seeking) {
      setState(state)
    }
  }

  const handleEnded = () => {
    console.log('onEnded')
    setState({ ...state, playing: state.loop })
  }

  const handleDuration = (duration:any) => {
    console.log('onDuration', duration)
    setState({ ...state, duration })
  }

  const handleClickFullscreen = () => {
    screenfull.request(findDOMNode(player))
  }

  const renderLoadButton = (url:any, label:any) => {
    return (
      <button onClick={() => load(url)}>
        {label}
      </button>
    )
  }

  const ref = (player:any) => {
    player = player
  }




  // const renderLoadButton = (url:string, label:any) => {
  //   return (
  //     <button
  //       className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full"
  //       onClick={() => load(url)}>
  //       {label}
  //     </button>
  //   )
  // }

  const { url, playing, controls, light, volume, muted, loop, played, loaded, duration, playbackRate, pip } = state
  const SEPARATOR = ' Â· '

  return (
    <div className="App">

      <div className="player-wrapper">
        <ReactPlayer
          className='react-player'
          ref={ref}
          width='100%'
          height='100%'
          url={url}
          pip={pip}
          playing={playing}
          controls={controls}
          light={light}
          loop={loop}
          playbackRate={playbackRate}
          volume={volume}
          muted={muted}
          onReady={() => console.log('onReady')}
          onStart={() => console.log('onStart')}
          onPlay={handlePlay}
          onEnablePIP={handleEnablePIP}
          onDisablePIP={handleDisablePIP}
          onPause={handlePause}
          onBuffer={() => console.log('onBuffer')}
          onSeek={e => console.log('onSeek', e)}
          onEnded={handleEnded}
          onError={e => console.log('onError', e)}
          onProgress={handleProgress}
          onDuration={handleDuration}
        />
        {/*<ReactPlayer
          className="react-player"
          url={'https://vimeo.com/575873877'}
          autoPlay={true}
          width={'100%'}
          height={'100%'}
          playing={state.playing}
        />*/}
       </div>

       <table>
        <tbody>
          <tr>
            <th>Controls</th>
            <td>
              <button onClick={handleStop}>Stop</button>
              <button onClick={handlePlayPause}>{playing ? 'Pause' : 'Play'}</button>
              <button onClick={handleClickFullscreen}>Fullscreen</button>
              {light &&
                <button onClick={() => player.showPreview()}>Show preview</button>}
              {ReactPlayer.canEnablePIP(url) &&
                <button onClick={handleTogglePIP}>{pip ? 'Disable PiP' : 'Enable PiP'}</button>}
            </td>
          </tr>
          <tr>
            <th>Speed</th>
            <td>
              <button onClick={handleSetPlaybackRate} value={1}>1x</button>
              <button onClick={handleSetPlaybackRate} value={1.5}>1.5x</button>
              <button onClick={handleSetPlaybackRate} value={2}>2x</button>
            </td>
          </tr>
          <tr>
            <th>Seek</th>
            <td>
              <input
                type='range' min={0} max={0.999999} step='any'
                value={played}
                onMouseDown={handleSeekMouseDown}
                onChange={handleSeekChange}
                onMouseUp={handleSeekMouseUp}
              />
            </td>
          </tr>
          <tr>
            <th>Volume</th>
            <td>
              <input type='range' min={0} max={1} step='any' value={volume} onChange={handleVolumeChange} />
            </td>
          </tr>
          <tr>
            <th>
              <label htmlFor='controls'>Controls</label>
            </th>
            <td>
              <input id='controls' type='checkbox' checked={controls} onChange={handleToggleControls} />
              <em>&nbsp; Requires player reload</em>
            </td>
          </tr>
          <tr>
            <th>
              <label htmlFor='muted'>Muted</label>
            </th>
            <td>
              <input id='muted' type='checkbox' checked={muted} onChange={handleToggleMuted} />
            </td>
          </tr>
          <tr>
            <th>
              <label htmlFor='loop'>Loop</label>
            </th>
            <td>
              <input id='loop' type='checkbox' checked={loop} onChange={handleToggleLoop} />
            </td>
          </tr>
          <tr>
            <th>
              <label htmlFor='light'>Light mode</label>
            </th>
            <td>
              <input id='light' type='checkbox' checked={light} onChange={handleToggleLight} />
            </td>
          </tr>
          <tr>
            <th>Played</th>
            <td><progress max={1} value={played} /></td>
          </tr>
          <tr>
            <th>Loaded</th>
            <td><progress max={1} value={loaded} /></td>
          </tr>
        </tbody>
      </table>


      <table>
        <tbody>
          <tr>
            <th>YouTube</th>
            <td>
              {renderLoadButton('https://www.youtube.com/watch?v=oUFJJNQGwhk', 'Test A')}
              {renderLoadButton('https://www.youtube.com/watch?v=jNgP6d9HraI', 'Test B')}
              {renderLoadButton('https://www.youtube.com/playlist?list=PLogRWNZ498ETeQNYrOlqikEML3bKJcdcx', 'Playlist')}
            </td>
          </tr>
          <tr>
            <th>SoundCloud</th>
            <td>
              {renderLoadButton('https://soundcloud.com/miami-nights-1984/accelerated', 'Test A')}
              {renderLoadButton('https://soundcloud.com/tycho/tycho-awake', 'Test B')}
              {renderLoadButton('https://soundcloud.com/yunghog/sets/doperaptraxxx', 'Playlist')}
            </td>
          </tr>
          <tr>
            <th>Facebook</th>
            <td>
              {renderLoadButton('https://www.facebook.com/facebook/videos/10153231379946729/', 'Test A')}
              {renderLoadButton('https://www.facebook.com/FacebookDevelopers/videos/10152454700553553/', 'Test B')}
            </td>
          </tr>
          <tr>
            <th>Vimeo</th>
            <td>
              {renderLoadButton('https://vimeo.com/90509568', 'Test A')}
              {renderLoadButton('https://vimeo.com/169599296', 'Test B')}
            </td>
          </tr>
          <tr>
            <th>Twitch</th>
            <td>
              {renderLoadButton('https://www.twitch.tv/videos/106400740', 'Test A')}
              {renderLoadButton('https://www.twitch.tv/videos/12783852', 'Test B')}
              {renderLoadButton('https://www.twitch.tv/kronovi', 'Test C')}
            </td>
          </tr>
          <tr>
            <th>Streamable</th>
            <td>
              {renderLoadButton('https://streamable.com/moo', 'Test A')}
              {renderLoadButton('https://streamable.com/ifjh', 'Test B')}
            </td>
          </tr>
          <tr>
            <th>Wistia</th>
            <td>
              {renderLoadButton('https://home.wistia.com/medias/e4a27b971d', 'Test A')}
              {renderLoadButton('https://home.wistia.com/medias/29b0fbf547', 'Test B')}
              {renderLoadButton('https://home.wistia.com/medias/bq6epni33s', 'Test C')}
            </td>
          </tr>
          <tr>
            <th>DailyMotion</th>
            <td>
              {renderLoadButton('https://www.dailymotion.com/video/x5e9eog', 'Test A')}
              {renderLoadButton('https://www.dailymotion.com/video/x61xx3z', 'Test B')}
            </td>
          </tr>
          <tr>
            <th>Mixcloud</th>
            <td>
              {renderLoadButton('https://www.mixcloud.com/mixcloud/meet-the-curators/', 'Test A')}
              {renderLoadButton('https://www.mixcloud.com/mixcloud/mixcloud-curates-4-mary-anne-hobbs-in-conversation-with-dan-deacon/', 'Test B')}
            </td>
          </tr>
          <tr>
            <th>Vidyard</th>
            <td>
              {renderLoadButton('https://video.vidyard.com/watch/YBvcF2BEfvKdowmfrRwk57', 'Test A')}
              {renderLoadButton('https://video.vidyard.com/watch/BLXgYCDGfwU62vdMWybNVJ', 'Test B')}
            </td>
          </tr>
          <tr>
            <th>Kaltura</th>
            <td>
              {renderLoadButton('https://cdnapisec.kaltura.com/p/2507381/sp/250738100/embedIframeJs/uiconf_id/44372392/partner_id/2507381?iframeembed=true&playerId=kaltura_player_1605622074&entry_id=1_jz404fbl', 'Test A')}
              {renderLoadButton('https://cdnapisec.kaltura.com/p/2507381/sp/250738100/embedIframeJs/uiconf_id/44372392/partner_id/2507381?iframeembed=true&playerId=kaltura_player_1605622336&entry_id=1_i1jmzcn3', 'Test B')}
            </td>
          </tr>
          <tr>
            <th>Files</th>
            <td>
              {renderLoadButton('https://test-videos.co.uk/vids/bigbuckbunny/mp4/h264/360/Big_Buck_Bunny_360_10s_1MB.mp4', 'mp4')}
              {renderLoadButton('https://test-videos.co.uk/vids/bigbuckbunny/webm/vp8/360/Big_Buck_Bunny_360_10s_1MB.webm', 'webm')}
              {renderLoadButton('https://filesamples.com/samples/video/ogv/sample_640x360.ogv', 'ogv')}
              {renderLoadButton('https://storage.googleapis.com/media-session/elephants-dream/the-wires.mp3', 'mp3')}
              <br />
              {renderLoadButton('https://bitdash-a.akamaihd.net/content/MI201109210084_1/m3u8s/f08e80da-bf1d-4e3d-8899-f0f6155f6efa.m3u8', 'HLS (m3u8)')}
              {renderLoadButton('http://dash.edgesuite.net/envivio/EnvivioDash3/manifest.mpd', 'DASH (mpd)')}
            </td>
          </tr>
          <tr>
            <th>Custom URL</th>
            <td>
              {/*<input ref={input => { urlInput = input }} type='text' placeholder='Enter URL' />
              <button onClick={() => setState({ ...state, url: urlInput.value })}>Load</button>*/}
            </td>
          </tr>
        </tbody>
      </table>

      <h2>State</h2>

      <table>
        <tbody>
          <tr>
            <th>url</th>
            <td className={!url ? 'faded' : ''}>
              {(url instanceof Array ? 'Multiple' : url) || 'null'}
            </td>
          </tr>
          <tr>
            <th>playing</th>
            <td>{playing ? 'true' : 'false'}</td>
          </tr>
          <tr>
            <th>volume</th>
            <td>{state.volume}</td>
          </tr>
          <tr>
            <th>played</th>
            <td>{state.played}</td>
          </tr>
          <tr>
            <th>loaded</th>
            <td>{state.loaded}</td>
          </tr>
          <tr>
            <th>duration</th>
            <td><Duration seconds={duration} /></td>
          </tr>
