import React, { useState } from 'react';
import './App.css';

import ReactPlayer from 'react-player'

interface Props {
  url: any | null
  pip: boolean
  playing: boolean
  controls: boolean
  light: boolean
  volume: number
  muted: boolean
  played: number
  loaded: number
  duration: number
  playbackRate: number
  loop: boolean
}

const VIDEO_URL = 'https://vimeo.com/575873877'

function App() {

  const [ state, setState ] = useState<Props>({
    url: null,
    pip: false,
    playing: false,
    controls: false,
    light: false,
    volume: 0.8,
    muted: false,
    played: 0,
    loaded: 0,
    duration: 0,
    playbackRate: 1.0,
    loop: false
  })

  const load = (url:any) => {
    setState({
      ...state,
      url,
      played: 0,
      loaded: 0,
      pip: false
    })
  }

  const handlePlayPause = () => {
    this.setState({ playing: !this.state.playing })
  }

  const handleStop = () => {
    this.setState({ url: null, playing: false })
  }

  const handleToggleControls = () => {
    const url = this.state.url
    this.setState({
      controls: !this.state.controls,
      url: null
    }, () => this.load(url))
  }

  const handleToggleLight = () => {
    this.setState({ light: !this.state.light })
  }

  const handleToggleLoop = () => {
    this.setState({ loop: !this.state.loop })
  }

  const handleVolumeChange = e => {
    this.setState({ volume: parseFloat(e.target.value) })
  }

  const handleToggleMuted = () => {
    this.setState({ muted: !this.state.muted })
  }

  const handleSetPlaybackRate = e => {
    this.setState({ playbackRate: parseFloat(e.target.value) })
  }

  const handleTogglePIP = () => {
    this.setState({ pip: !this.state.pip })
  }

  const handlePlay = () => {
    console.log('onPlay')
    this.setState({ playing: true })
  }

  const handleEnablePIP = () => {
    console.log('onEnablePIP')
    this.setState({ pip: true })
  }

  const handleDisablePIP = () => {
    console.log('onDisablePIP')
    this.setState({ pip: false })
  }

  const handlePause = () => {
    console.log('onPause')
    this.setState({ playing: false })
  }

  const handleSeekMouseDown = e => {
    this.setState({ seeking: true })
  }

  const handleSeekChange = e => {
    this.setState({ played: parseFloat(e.target.value) })
  }

  const handleSeekMouseUp = e => {
    this.setState({ seeking: false })
    this.player.seekTo(parseFloat(e.target.value))
  }

  const handleProgress = state => {
    console.log('onProgress', state)
    // We only want to update time slider if we are not currently seeking
    if (!this.state.seeking) {
      this.setState(state)
    }
  }

  const handleEnded = () => {
    console.log('onEnded')
    this.setState({ playing: this.state.loop })
  }

  const handleDuration = (duration) => {
    console.log('onDuration', duration)
    this.setState({ duration })
  }

  const handleClickFullscreen = () => {
    screenfull.request(findDOMNode(this.player))
  }

  const renderLoadButton = (url, label) => {
    return (
      <button onClick={() => this.load(url)}>
        {label}
      </button>
    )
  }

  const ref = player => {
    this.player = player
  }




  // const renderLoadButton = (url:string, label:any) => {
  //   return (
  //     <button
  //       className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full"
  //       onClick={() => load(url)}>
  //       {label}
  //     </button>
  //   )
  // }

  const { url, playing, controls, light, volume, muted, loop, played, loaded, duration, playbackRate, pip } = state
  const SEPARATOR = ' · '

  return (
    <div className="App">

      <div className="player-wrapper">
        <ReactPlayer
          ref={ref}
          className='react-player'
          width='100%'
          height='100%'
          url={url}
          pip={pip}
          playing={playing}
          controls={controls}
          light={light}
          loop={loop}
          playbackRate={playbackRate}
          volume={volume}
          muted={muted}
          onReady={() => console.log('onReady')}
          onStart={() => console.log('onStart')}
          // onPlay={handlePlay}
          // onEnablePIP={handleEnablePIP}
          // onDisablePIP={handleDisablePIP}
          // onPause={handlePause}
          onBuffer={() => console.log('onBuffer')}
          onSeek={e => console.log('onSeek', e)}
          // onEnded={handleEnded}
          onError={e => console.log('onError', e)}
          // onProgress={handleProgress}
          // onDuration={handleDuration}
        />
        {/*<ReactPlayer
          className="react-player"
          url={'https://vimeo.com/575873877'}
          autoPlay={true}
          width={'100%'}
          height={'100%'}
          playing={state.playing}
        />*/}
       </div>

       <div className="player-ui">
          <input id='controls' type='checkbox' checked={controls} onChange={handleToggleControls} />
          <em>&nbsp; Requires player reload</em>
         {renderLoadButton(VIDEO_URL, 'Play')}
         {/*<button 
           className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full"
           type="button"
           onClick={onClick.play}>Play</button>*/}

         {/*<button onClick={onClick.pause}>{state ? 'Pause' : 'Play'}</button>*/}
       </div>
    </div>
  );
}

export default App;
