  console.log(voices);

  const handleClick = () => {
    window.speechSynthesis.speak(speech);

    // setTimeout(() => {
    //   speechSynthesis.onvoiceschanged = 'ja-JP';
    // })
  }

  return (
    <div className="App">
      <button type="button" onClick={handleClick}>Click me!</button>
    </div>
  );
}

export default App;
