import React from 'react';
import logo from './logo.svg';
import './App.css';

const randomCounts = () => {

}

let timer;

function App() {

  let speech = new SpeechSynthesisUtterance();
  speech.lang = "en";
  speech.text = '10000';
  speech.rate = 1;
  speech.volume = 1;
  speech.pitch = 1; 

  const handleClick = () => {
    window.speechSynthesis.speak(speech);

    timer = setTimeout(() => {
      speech.lang = "ja";
      window.speechSynthesis.speak(speech);

    }, 2000);
  }

  return (
    <div className="App">
      {/* <select>
        { }
      </select> */}
      <button 
        className="bg-blue hover:bg-blue-light text-white font-bold py-2 px-4 border-b-4 border-blue-dark hover:border-blue rounded"
        type="button" onClick={handleClick}>Click me!</button>
    </div>
  );
}

export default App;
