function getVoices() {
  const voices = speechSynthesis.getVoices();
  let values = '';
  for(var i = 0; i < voices.length ; i++) {
    const lang = voices[i].lang;
    const value = voices[i].name + ' / ' + lang
    values += value + '\n';
    return (
      <option 
        data-lang={voices[i].lang}
        data-name={voices[i].name}
        >
          {value}
        </option>
    )
  }
}

