interface RequestProps {
  input: any
  voice: any
  audioConfig: any
}

function App() {

  let speech = new SpeechSynthesisUtterance();
  speech.lang = "en";
  speech.text = '10000';
  speech.rate = 1;
  speech.volume = 1;
  speech.pitch = 1;
  let voices = [];

  const handleClick = () => {
    window.speechSynthesis.speak(speech);
  }

  return (
    <div className="App">
      <button type="button" onClick={handleClick}>Click me!</button>
    </div>
  );
}

export default App;
