import React from 'react';
import logo from './logo.svg';
import './App.css';

const randomCounts = () => {
  const number1 = Math.round(Math.random() * 100);
  const number2 = Math.round(Math.random() * 100);
  const val = Math.floor(number1 * number2);

  return val.toString();
}

let timer:any;

function App() {

  let speech = new SpeechSynthesisUtterance();
  speech.rate = 1;
  speech.volume = 1;
  speech.pitch = 1;

  const resetVal = () => {
    speech.lang = "en";
    speech.text = randomCounts();
  }


  const handleClick = () => {
    window.speechSynthesis.speak(speech);

    clearTimeout(timer);
    timer = setTimeout(() => {
      speech.lang = "ja";
      window.speechSynthesis.speak(speech);

    }, 2000);
  }

  return (
    <div className="App">

      <button 
        className="inline-block px-6 py-2 text-xs font-medium leading-6 text-center text-white uppercase transition bg-red-500 rounded-full shadow ripple hover:shadow-lg hover:bg-red-600 focus:outline-none"
        type="button" onClick={handleClick}>Click me!</button>
    </div>
  );
}

export default App;
