import React from 'react';
import logo from './logo.svg';
import './App.css';

const DELAY = 2000;
const randomCounts = () => {
  const number1 = Math.round(Math.random() * 100);
  const number2 = Math.round(Math.random() * 100);
  const val = Math.floor(number1 * number2);

  return val.toString();
}

let timer:any;

function App() {

  let speech = new SpeechSynthesisUtterance();
  speech.rate = 1;
  speech.volume = 1;
  speech.pitch = 1;

  const resetVal = () => {
    speech.lang = "en";
    speech.text = randomCounts();
  }

  const chaseJp = () => {
    speech.lang = "ja";
    window.speechSynthesis.speak(speech);
  }

  const handleClick = () => {
    resetVal();
    window.speechSynthesis.speak(speech);

    clearTimeout(timer);
    timer = setTimeout(chaseJp, DELAY);
  }

  return (
    <div className="App">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-light-blue-500 shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl"></div>
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <button 
            className="inline-block px-6 py-2 text-xs font-medium leading-6 text-center text-white uppercase transition bg-red-500 rounded-full shadow ripple hover:shadow-lg hover:bg-red-600 focus:outline-none"
            type="button" onClick={handleClick}>Click me!</button>
        </div>
      </div>
    </div>
  );
}

export default App;
